import { h, // eslint-disable-line @typescript-eslint/no-unused-vars
 } from '@stencil/core';
import { COLUMN_DEF_DATATYPE_INTEGER, COLUMN_DEF_DATATYPE_KEY } from '../../modus-table.constants';
import ModusTableCellContent from './modus-table-cell-content';
import ModusTableCellExpandIcons from './modus-table-cell-expand-icons';
export const ModusTableCell = ({ cell, row, cellIndex, rowsExpandable, frozenColumns, onLinkClick, }) => {
  const { id, column } = cell;
  return (h("td", { key: id, class: `
          ${frozenColumns.includes(column.id) ? 'sticky-left' : ''}
          ${column.getIsResizing() ? 'active-resize' : ''}
      `, style: {
      width: `${column.getSize()}px`,
    } }, h("div", { class: `table-cell wrap-text${column.columnDef[COLUMN_DEF_DATATYPE_KEY] === COLUMN_DEF_DATATYPE_INTEGER ? 'text-align-right' : ''}` }, 
  /** Expand or collaps icon */
  rowsExpandable ? h(ModusTableCellExpandIcons, { cellIndex: cellIndex, row: row }) : '', 
  /** Cell content */
  h(ModusTableCellContent, { cell: cell, onLinkClick: (link) => {
      onLinkClick(link);
    } }))));
};
